// import logo from './logo.svg';
import './App.css';
import { Map, Marker, GoogleApiWrapper, Polygon, InfoWindow } from 'google-maps-react';
import React, { Component } from 'react';
import { Row, Col, Button } from 'reactstrap';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      markers: [],
      showingInfoWindow: false,
      activeMarker: {},
      selectedPlace: {},
      triangleCoords: [],
      triangleCoordsTemp: []
    };
    this.onClick = this.onClick.bind(this);
  }

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onMapClicked = (props) => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      })
    }
  };

  onClick(t, map, coord) {
    const { latLng } = coord;
    const lat = latLng.lat();
    const lng = latLng.lng();

    this.setState(previousState => {
      return {
        markers: [
          ...previousState.markers,
          {
            title: "Location",
            name: "Location",
            position: { lat, lng }
          }
        ],
        triangleCoordsTemp: {
          lat, lng
        }
      };
    });
    let tempLat = 0;
    let tempLeng = 0;
    for( let i = 0; i < 1; i++){
      tempLat = this.state.triangleCoordsTemp.lat
      tempLeng = this.state.triangleCoordsTemp.lng
    }
    for( let i = 0; i < 1; i++){
      this.state.triangleCoords.push({ lat: tempLat, lng: tempLeng },)
    }
    alert(JSON.stringify(this.state.triangleCoords.length))
  }

  clearMark(){
    this.setState({
      markers: [], triangleCoords: []
    })
  }

  viewPolygon() {
    if(this.state.triangleCoords.length >= 3 || this.state.triangleCoords >= 4 || this.state.triangleCoords >= 5){
      return(
          <Polygon
            paths={this.state.triangleCoords}
            strokeColor="#0000FF"
            strokeOpacity={0.8}
            strokeWeight={2}
            fillColor="#0000FF"
            fillOpacity={0.35} />
      )
    }
  }

  render() {
    return (
      <div className="App flex-column">
        <Col md="12">
        <Row className="mb-3">
          <div className="d-flex flex-column">
            <div className="p-2 col-example text-right">Mission</div>
            <div className="p-2 col-example text-left">Add</div>
            <div className="p-2 col-example text-left">Start</div>
            <div className="p-2 col-example text-left">End</div>
            <div onClick={() => { this.clearMark() }} className="p-2 text-right">Clear</div>
          </div>
          </Row>
          <Row className="mt-1">
            <div className="Maps">
              <Map google={this.props.google}
                  initialCenter={{lat: -6.2710423,lng: 106.7962754}}
                style={{width: '70%', height: '100%', position: 'relative', marginTop: '-20%'}}
                className={'map'}
                zoom={16}
                onClick={this.onClick}>
                {this.viewPolygon()}
                <InfoWindow
                  marker={this.state.activeMarker}
                  visible={this.state.showingInfoWindow}>
                    <div className="TitleWindow">
                      <h1>TEST</h1>
                    </div>
                </InfoWindow>

                {this.state.markers.map((marker, index) => (
                  <Marker
                    onClick={this.onMarkerClick}
                    key={index}
                    title={marker.title}
                    name={marker.name}
                    position={marker.position}
                  />
                ))}
              </Map>
            </div>
          </Row>
        </Col>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: ('AIzaSyBAefhRlXEH3vCko-zZTX6PHllTR6av4WI')
})(App)
