// import logo from './logo.svg';
import './App.css';
import { Map, Marker, GoogleApiWrapper, Polygon } from 'google-maps-react';
import React, { Component } from 'react';
import { Row, Col } from 'reactstrap';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      markers: [
        {
          title: "ABC",
          name: "SUCCESS",
          position: { lat: -6.2710423, lng: 106.7962754 }
        }
      ]
    };
    this.onClick = this.onClick.bind(this);
  }

  onClick(t, map, coord) {
    const { latLng } = coord;
    const lat = latLng.lat();
    const lng = latLng.lng();

    this.setState(previousState => {
      return {
        markers: [
          ...previousState.markers,
          {
            title: "",
            name: "",
            position: { lat, lng }
          }
        ]
      };
    });
  }

  render() {
    const triangleCoords = [
      {lat: -6.2710423, lng: 106.7962754},
      {lat: -6.2715431, lng: 106.7951503},
      {lat: -6.2720115, lng: 106.7971688},
    ];

    return (
      <div className="App flex-column">
        <Col md="12">
          <Row className="mt-1">
            <div className="Maps">
              <Map google={this.props.google}
                  initialCenter={{lat: -6.2710423,lng: 106.7962754}}
                style={{width: '70%', height: '100%', position: 'relative'}}
                className={'map'}
                zoom={16}>
                {/* <Polygon
                  paths={triangleCoords}
                  strokeColor="#0000FF"
                  strokeOpacity={0.8}
                  strokeWeight={2}
                  fillColor="#0000FF"
                  fillOpacity={0.35} />
                <Marker
                  title={'Vehicle 1'}
                  name={'Start1'}
                  position={{ lat: -6.2715431, lng: 106.7951503 }} />
                <Marker
                  title={'Vehicle 1'}
                  name={'Target1'}
                  position={{ lat: -6.2720115, lng: 106.7971688 }} />
                <Marker /> */}
                {this.state.markers.map((marker, index) => (
                  <Marker
                    key={index}
                    title={marker.title}
                    name={marker.name}
                    position={marker.position}
                  />
                ))}
              </Map>
            </div>
          </Row>
          <Row className="mb-3">
          <div className="d-flex flex-column">
            <div className="p-2 col-example text-right">Mission</div>
          </div>
          <div className="d-flex flex-column-reverse">
            <div className="p-2 col-example text-left">Add</div>
            <div className="p-2 col-example text-left">Start</div>
            <div className="p-2 col-example text-left">End</div>
            <div className="p-2 col-example text-left">Clear</div>
          </div>
          </Row>
        </Col>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: ('AIzaSyBAefhRlXEH3vCko-zZTX6PHllTR6av4WI')
})(App)
