// import logo from './logo.svg';
import './App.css';
import { Map, Marker, GoogleApiWrapper, Polygon, InfoWindow } from 'google-maps-react';
import React, { Component } from 'react';
import { Row, Col, Button } from 'reactstrap';
import camera from './icon/cam.png';
import video from './icon/video.png'

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      markers: [],
      showingInfoWindow: false,
      activeMarker: {},
      selectedPlace: {},
      triangleCoords: [],
      triangleCoordsTemp: [],
      addMark: false
    };
    this.onClick = this.onClick.bind(this);
  }

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onMapClicked = (props) => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      })
    }
  };

  onClick(t, map, coord) {
    const { latLng } = coord;
    const lat = latLng.lat();
    const lng = latLng.lng();

    this.setState(previousState => {
      return {
        markers: [
          ...previousState.markers,
          {
            title: "Location",
            name: "Location",
            position: { lat, lng }
          }
        ],
        triangleCoordsTemp: {
          lat, lng
        }
      };
    });
    let tempLat = 0;
    let tempLeng = 0;
    for( let i = 0; i < 1; i++){
      tempLat = this.state.triangleCoordsTemp.lat
      tempLeng = this.state.triangleCoordsTemp.lng
    }
    for( let i = 0; i < 1; i++){
      this.state.triangleCoords.push({ lat: tempLat, lng: tempLeng },)
    }
    // alert(JSON.stringify(this.state.triangleCoords.length))
  }

  clearMark(){
    this.setState({
      markers: [], triangleCoords: []
    })
  }

  addMark(){
    this.setState({
      addMark: true
    })
  }

  endMark() {
    this.setState({
      addMark: false
    })
  }

  viewPolygon() {
    if(this.state.triangleCoords.length >= 5){
      return(
          <Polygon
            paths={this.state.triangleCoords}
            strokeColor="#0000FF"
            strokeOpacity={0.8}
            strokeWeight={2}
            fillColor="#0000FF"
            fillOpacity={0.35} />
      )
    }
  }

  showList(){
    // let tempList = [];
    // for(i=0; i < 5; i++){
    //   tempList.push(this.state.triangleCoords)
    // }
    if(this.state.triangleCoords.length === 0){
      return(null)
    } else if(this.state.triangleCoords.length === 1){
      return(
        <div className="d-flex2 flex-column">
          <div className="p-2 col-example text-right">{this.state.triangleCoords.lat}<img src={video} style={{width: 20, height: 20, marginRight: 10}} /><img src={camera} width="20" height="20" /></div>
        </div>
      )
    } else if (this.state.triangleCoords.length === 2){
      return(
        <div className="d-flex2 flex-column">
          <div className="p-2 col-example text-right">{this.state.triangleCoords.lat}<img src={video} style={{width: 20, height: 20, marginRight: 10}} /><img src={camera} width="20" height="20" /></div>
          <div className="p-2 col-example text-right">{this.state.triangleCoords.lat}<img src={video} style={{width: 20, height: 20, marginRight: 10}} /><img src={camera} width="20" height="20" /></div>
        </div>
      )
    } else if (this.state.triangleCoords.length === 3){
      return(
        <div className="d-flex2 flex-column">
          <div className="p-2 col-example text-right">{this.state.triangleCoords.lat}<img src={video} style={{width: 20, height: 20, marginRight: 10}} /><img src={camera} width="20" height="20" /></div>
          <div className="p-2 col-example text-right">{this.state.triangleCoords.lat}<img src={video} style={{width: 20, height: 20, marginRight: 10}} /><img src={camera} width="20" height="20" /></div>
          <div className="p-2 col-example text-right">{this.state.triangleCoords.lat}<img src={video} style={{width: 20, height: 20, marginRight: 10}} /><img src={camera} width="20" height="20" /></div>
        </div>
      )
    } else if (this.state.triangleCoords.length === 4){
      return(
        <div className="d-flex2 flex-column">
          <div className="p-2 col-example text-right">{this.state.triangleCoords.lat}<img src={video} style={{width: 20, height: 20, marginRight: 10}} /><img src={camera} width="20" height="20" /></div>
